  render() {
    const { artistName } = this.state;

    return (
      <form>
        <label htmlFor="name">Enter a musician</label>
        <input
          type="text"
          name="artistName"
          id="artistName"
          value={artistName}
          onChange={this.handleChange} />
        <input
          type="button"
          value="Submit"
          onClick={this.submitForm} />
      </form>
    );
  }
}

export default Form;
