import React, {Component} from 'react'
import Table from './Table.js'
import Form from './Form.js'
const server = require("./server.js");


class App extends Component {
  state = {
    names: [],
    responses: []
  }

  removeName = (index) => {
    const {names} = this.state

    this.setState({
      names: names.filter((name, i) => {
        return i !== index
      }),
    })
  }

  handleSubmitName = (name) => {
    this.setState({names: [...this.state.names, name]})

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: this.state.names
    };
    // fetch('https://jsonplaceholder.typicode.com/posts', requestOptions)
    fetch('/', requestOptions)
        .then(response => response.json())
        .then(data => this.setState({ responses: [...this.state.responses, JSON.stringify(data.id) + Math.floor(Math.random() * 10)] }))
        // .then(data => alert(JSON.stringify(data.id)));

  }


  render() {
    const { names } = this.state
    const { responses } = this.state;

    return (
      <div className="container">
        <Table nameData={names} removeName={this.removeName} responses={responses} />
        {responses}
        <Form handleSubmitName={this.handleSubmitName} />
      </div>
    )
  }
}

export default App
