import React, {Component} from 'react'
import Table from './Table.js'
import Form from './Form.js'


class App extends Component {
  state = {
    names: [],
    responses: []
  }

  removeName = (index) => {
    const {names} = this.state

    this.setState({
      names: names.filter((name, i) => {
        return i !== index
      }),
    })
  };


  handleSubmitName = (name) => {
    this.setState({names: [...this.state.names, name]})

    const requestOptions = {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        // body: this.state.names
    };


    let i = Math.floor(Math.random() * 10)

    fetch('https://jsonplaceholder.typicode.com/comments?postId=' + i, requestOptions)
        .then(response => response.json())
        .then(data => this.setState({ responses: [...this.state.responses, { postId: data[0].postId, id: data[0].id, name: data[0].name, email: data[0].email } ] }))
        // .then(data => alert(data[0].id));

    alert(JSON.stringify(this.state.names));
    alert(JSON.stringify(this.state.responses));
  }


  render() {
    const { names } = this.state
    const { responses } = this.state;

    return (
      <div id="parent">
        <div className="container">
          <Table responsesData={responses} />
        </div>
        <div className=".small-container">
          <Form handleSubmitName={this.handleSubmitName} />
        </div>
      </div>
    )
  }
}

export default App
