import React, {Component} from 'react'

class Form extends Component {
  initialState = {
    firstMusician: '',
    secondMusician: '',
    thirdMusician: ''
  }

  state = this.initialState

  handleChange = (event) => {
    const {name, value} = event.target

    this.setState({
      [name]: value
    })
  }

  submitForm = () => {
    this.props.handleSubmit(this.state)
    this.setState(this.initialState)
  }

  render() {
    const { firstMusician, secondMusician, thirdMusician } = this.state;

    return (
      <form>
        <label htmlFor="name">First Musician</label>
        <input
          type="text"
          name="name"
          id="name"
          value={firstMusician}
          onChange={this.handleChange} />
        <input
          type="button"
          value="Submit"
          onClick={this.submitForm} />
        <label htmlFor="name">Second Musician</label>
        <input
            type="text"
            name="name"
            id="name"
            value={secondMusician}
            onChange={this.handleChange} />
        <input
          type="button"
          value="Submit"
          onClick={this.submitForm} />
        <label htmlFor="name">Third Musician</label>
        <input
            type="text"
            name="name"
            id="name"
            value={thirdMusician}
            onChange={this.handleChange} />
        <input
          type="button"
          value="Submit"
          onClick={this.submitForm} />
      </form>
    );
  }
}

export default Form;
