import React, {Component} from 'react'
import './server.js'

class Form extends Component {
  initialState = {
    artistName: '',
    date: '',
    image: ''
  }

  state = this.initialState

  handleChange = (event) => {
    const {name, value} = event.target

    this.setState({
      [name]: value,
    })
  }

  submitForm = () => {
    this.props.handleSubmit(this.state)
    this.setState(this.initialState)
  }

  componentDidMount() {
    fetch("http://localhost:3000/", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            artistName: result.recommendation,
            date: result.release_date,
            image: result.album_image.url,
          });
        },
        (error) => {
          this.setState({
            error
          });
        }
      )
  }
  render() {
    const { artistName, date, image } = this.state;

    return (
      <form>
        <label htmlFor="name">Enter a musician</label>
        <input
          type="text"
          name="artistName"
          id="artistName"
          value={artistName}
          onChange={this.handleChange} />
        <input
          type="button"
          value="Submit"
          onClick={this.submitForm} />
      </form>
    );
  }
}

export default Form;
