import React, {Component} from 'react'
import Table from './Table.js'
import Form from './Form.js'


class App extends Component {
  state = {
    names: []
  }

  removeName = (index) => {
    const {names} = this.state

    this.setState({
      names: names.filter((name, i) => {
        return i !== index
      }),
    })
  }

  handleSubmit = (name) => {
    this.setState({names: [...this.state.names, name]})

    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: this.state.names
    };
    fetch('https://jsonplaceholder.typicode.com/posts', requestOptions)
        .then(response => response.json())
        .then(data => this.setState({ postId: data.id + Math.floor(Math.random() * 10) }));

    alert(this.state.artistName);
    alert(this.state.postId);
  }

  render() {
    const { names } = this.state
    const { postId } = this.state;
    const { artistName } = this.state;

    return (
      <div className="container">
        <Table nameData={names} removeName={this.removeName} />
        <Form handleSubmit={this.handleSubmit} />
        Returned Id: {postId}

        Returned artistName: {artistName}
      </div>
    )
  }
}

export default App
