import React, {Component} from 'react'
import Table from './Table.js'
import Form from './Form.js'

class App extends Component {
  state = {
    names: [],
    responses: []
  }

  removeName = (index) => {
    const {names} = this.state

    this.setState({
      names: names.filter((name, i) => {
        return i !== index
      }),
    })
  };


  handleSubmitName = (name) => {
    this.setState({names: [...this.state.names, name]})

    const requestOptions = {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        // body: this.state.names
    };


    let i = Math.floor(Math.random() * 10)

    fetch('https://jsonplaceholder.typicode.com/comments?postId=' + i, requestOptions)
        .then(response => response.json())
        .then(data => this.setState({ responses: [...this.state.responses, { postId: data[0].body, id: data[0].id, name: data[0].name, email: data[0].email } ] }))
  }

  render() {
    const { names } = this.state
    const { responses } = this.state;

    const inputSize = this.state.names.length;
    let inputForm;
    if (inputSize < 3) {
      inputForm = <Form handleSubmitName={this.handleSubmitName} />
    } else if (inputSize == 3) {
      if (this.state.responses[0].id == this.state.responses[1].id && this.state.responses[1].id == this.state.responses[2].id) {
        inputForm = ' 🎉🎉🎉 You won! Refresh to play again.'
      } else {
        inputForm = '😭😭😭 You lost...Refresh to try again'
      }
    }

    return (
      <div id="parent" className="body">
        <div className="container">
          <Table responsesData={responses} />
        </div>
        <div className="container">
          {inputForm}
        </div>
      </div>
    )
  }
}

export default App
